package io.nuls.offlinecontract.deserialization;

import io.nuls.base.basic.NulsByteBuffer;
import io.nuls.contract.model.txdata.CreateContractData;
import io.nuls.core.exception.NulsException;
import io.nuls.core.parse.JSONUtils;
import org.apache.commons.io.FileUtils;
import org.bouncycastle.util.encoders.Hex;
import org.junit.Test;

import java.io.File;
import java.io.IOException;

/**
 * @author: PierreLuo
 * @date: 2019-11-11
 */
public class ContractDataDeserialization {

    @Test
    public void testCreateContractData() throws NulsException, IOException {
        String dataHex = "";
        CreateContractData data = new CreateContractData();
        data.parse(new NulsByteBuffer(Hex.decode(dataHex)));
        CreateContractDataDto dto = new CreateContractDataDto(data);
        System.out.println(JSONUtils.obj2PrettyJson(dto));
    }
}
